import React, { Component, Fragment } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    counter: 0
  }

  render(){
    const {counter} = this.state
    return (
      <div className="app">
        <div className="display">Total Counter is {counter}</div>
        <Fragment>
          <button className="btn decrement" onClick={ () => this.setState(({counter} )=> ({ counter: counter - 1 }))}>Decrement</button>
          <button className="btn increment" onClick={ () => this.setState(({counter} )=> ({ counter: counter + 1 }))}>Increment</button>
        </Fragment>
      </div>
    );
  }

}

export default App;
